parameters.jsCode:
// Obtén el JSON de la respuesta
const data = $input.all()[0].json.output;

// Extraer datos generales
const summary = data.summary || {};
const callDate = data.call_status?.date || "No especificada";

// Array para almacenar todas las filas
const allRows = [];

// Función para procesar cada categoría
function processCategory(categoryKey, categoryName, expensesData) {
  if (expensesData?.items && Array.isArray(expensesData.items)) {
    expensesData.items.forEach((item, index) => {
      allRows.push({
        id: `${categoryKey}-${index + 1}`, // Generar ID único
        date: callDate,
        Services: categoryName,
        amount: item.amount || 0,
        vendor: item.vendor || "No especificado",
        description: item.description || "Sin descripción",
        payment_method: item.payment_method || "No especificado",
        has_receipt: item.has_receipt ? "Sí" : "No",
        // total_expenses: summary.total_expenses || 0,
        pending_documentation: summary.pending_documentation?.join(", ") || "Ninguna",
        needs_follow_up: summary.needs_follow_up ? "Sí" : "No",
        next_action: summary.next_action || "Ninguna"
      });
    });
  }
}

// Procesar todas las categorías
const categories = [
  { key: "materials", name: "Materiales" },
  { key: "labor", name: "Mano de Obra" },
  { key: "equipment", name: "Equipos" },
  { key: "transportation", name: "Transporte" },
  { key: "permits", name: "Permisos" },
  { key: "construction_expenses", name: "Gastos de Obra" }
];

categories.forEach(({ key, name }) => {
  if (data.expenses && data.expenses[key]) {
    processCategory(key, name, data.expenses[key]);
  }
});

return allRows;

