function transformarYProcesarDatos(datosImagen) {
    try {
        // Funciones auxiliares
        function calculoMargenUY(nominal, liquido) {
            const minimoLegalUY = nominal * 0.35;
            const diferencia = liquido - minimoLegalUY;
            const margenMinimo = 3000;
            return {
                tieneMargen: diferencia > margenMinimo,
                margenDisponible: diferencia
            };
        }

        function obtenerMensajeSegunTipo(tipoUsuario, resultado) {
            return resultado.tieneMargen 
                ? obtenerMensajeAprobado(tipoUsuario)
                : obtenerMensajeRechazado(tipoUsuario);
        }

        function obtenerMensajeAprobado(tipoUsuario) {
            if (tipoUsuario === 'empleado') {
                return `Está en condiciones de solicitar un crédito. Por favor, envíenos fotos nítidas y legibles de la siguiente documentación:\n\n• 𝗖𝗲́𝗱𝘂𝗹𝗮 𝗱𝗲 𝗶𝗱𝗲𝗻𝘁𝗶𝗱𝗮𝗱 𝘃𝗶𝗴𝗲𝗻𝘁𝗲\n• 𝗨́𝗹𝘁𝗶𝗺𝗼𝘀 𝗱𝗼𝘀 𝗿𝗲𝗰𝗶𝗯𝗼𝘀 𝗱𝗲 𝘀𝘂𝗲𝗹𝗱𝗼\n• 𝗖𝗼𝗻𝘀𝘁𝗮𝗻𝗰𝗶𝗮 𝗱𝗲 𝗱𝗼𝗺𝗶𝗰𝗶𝗹𝗶𝗼 (𝗽𝘂𝗲𝗱𝗲 𝘀𝗲𝗿 𝘂𝗻 𝗿𝗲𝗰𝗶𝗯𝗼 𝗱𝗲 𝗨𝗧𝗘, 𝗢𝗦𝗘, 𝗔𝗡𝗧𝗘𝗟, 𝗲𝘀𝘁𝗮𝗱𝗼 𝗱𝗲 𝗰𝘂𝗲𝗻𝘁𝗮, 𝗲𝘁𝗰.) \n\n𝗨𝗻𝗮 𝘃𝗲𝘇 𝗿𝗲𝗰𝗶𝗯𝗶𝗱𝗼𝘀, 𝗽𝗿𝗼𝗰𝗲𝗱𝗲𝗿𝗲𝗺𝗼𝘀 𝗮 𝗲𝘃𝗮𝗹𝘂𝗮𝗿 𝘀𝘂 𝘀𝗼𝗹𝗶𝗰𝗶𝘁𝘂𝗱, 𝗱𝗲𝗻𝘁𝗿𝗼 𝗱𝗲𝗹 𝗵𝗼𝗿𝗮𝗿𝗶𝗼 𝗱𝗲 𝗮𝘁𝗲𝗻𝗰𝗶𝗼́𝗻 𝗮𝗹 𝗽𝘂́𝗯𝗹𝗶𝗰𝗼, 𝗹𝘂𝗻𝗲𝘀 𝗮 𝘃𝗶𝗲𝗿𝗻𝗲𝘀 𝗱𝗲 𝟵 𝗮 𝟭𝟳 𝗵𝗼𝗿𝗮𝘀.`;
            } else {
                return `Está en condiciones de solicitar un crédito. Por favor, envíenos fotos nítidas y legibles de la siguiente documentación:\n\n• 𝗖𝗲́𝗱𝘂𝗹𝗮 𝗱𝗲 𝗶𝗱𝗲𝗻𝘁𝗶𝗱𝗮𝗱 𝘃𝗶𝗴𝗲𝗻𝘁𝗲\n• 𝗨́𝗹𝘁𝗶𝗺𝗼𝘀 𝗱𝗼𝘀 𝗿𝗲𝗰𝗶𝗯𝗼𝘀 𝗱𝗲 𝗹𝗮 𝗽𝗮𝘀𝗶𝘃𝗶𝗱𝗮𝗱\n• 𝗖𝗼𝗻𝘀𝘁𝗮𝗻𝗰𝗶𝗮 𝗱𝗲 𝗱𝗼𝗺𝗶𝗰𝗶𝗹𝗶𝗼 (𝗽𝘂𝗲𝗱𝗲 𝘀𝗲𝗿 𝘂𝗻 𝗿𝗲𝗰𝗶𝗯𝗼 𝗱𝗲 𝗨𝗧𝗘, 𝗢𝗦𝗘, 𝗔𝗡𝗧𝗘𝗟, 𝗲𝘀𝘁𝗮𝗱𝗼 𝗱𝗲 𝗰𝘂𝗲𝗻𝘁𝗮, 𝗲𝘁𝗰.) \n\n𝗨𝗻𝗮 𝘃𝗲𝘇 𝗿𝗲𝗰𝗶𝗯𝗶𝗱𝗼𝘀, 𝗽𝗿𝗼𝗰𝗲𝗱𝗲𝗿𝗲𝗺𝗼𝘀 𝗮 𝗲𝘃𝗮𝗹𝘂𝗮𝗿 𝘀𝘂 𝘀𝗼𝗹𝗶𝗰𝗶𝘁𝘂𝗱, 𝗱𝗲𝗻𝘁𝗿𝗼 𝗱𝗲𝗹 𝗵𝗼𝗿𝗮𝗿𝗶𝗼 𝗱𝗲 𝗮𝘁𝗲𝗻𝗰𝗶𝗼́𝗻 𝗮𝗹 𝗽𝘂́𝗯𝗹𝗶𝗰𝗼, 𝗹𝘂𝗻𝗲𝘀 𝗮 𝘃𝗶𝗲𝗿𝗻𝗲𝘀 𝗱𝗲 𝟵 𝗮 𝟭𝟳 𝗵𝗼𝗿𝗮𝘀.`;
            }
        }

        function obtenerMensajeRechazado(tipoUsuario) {
            return `Gracias por su consulta. Por ahora no cuenta con el margen necesario, puede consultar más adelante.`;
        }

        // Verificar y extraer los datos
        if (!datosImagen || !datosImagen.output || !datosImagen.output.output) {
            throw new Error("Estructura de datos inválida o incompleta");
        }

        const documentData = datosImagen.output.output.document_data;
        const amounts = documentData.amounts;

        // Extraer y validar montos
        const montoNominal = parseFloat(amounts.nominal);
        const montoLiquido = parseFloat(amounts.liquid);

        if (isNaN(montoNominal) || isNaN(montoLiquido)) {
            throw new Error("Los montos no son números válidos");
        }

        // Validaciones adicionales
        if (montoLiquido >= montoNominal) {
            throw new Error("El monto líquido no puede ser mayor o igual al monto nominal");
        }

        if (montoNominal < 0 || montoLiquido < 0) {
            throw new Error("Los montos no pueden ser negativos");
        }

        // Calcular margen y obtener mensaje
        const resultado = calculoMargenUY(montoNominal, montoLiquido);
        const tipoUsuario = documentData.issuer?.name?.toUpperCase().includes('BPS') ? 'jubilado' : 'empleado';
        const mensajeRespuesta = obtenerMensajeSegunTipo(tipoUsuario, resultado);

        // Retornar respuesta completa
        return {
            status: "success",
            message: mensajeRespuesta,
            data: {
                tipoUsuario,
                tieneMargen: resultado.tieneMargen,
                margenDisponible: resultado.margenDisponible,
                parametros: {
                    parameters: tipoUsuario === 'jubilado' ? {
                        montoNominal_jubilado: montoNominal.toString(),
                        montoLiquido_jubilado: montoLiquido.toString()
                    } : {
                        montoNominal_empleado: montoNominal.toString(),
                        montoLiquido_empleado: montoLiquido.toString()
                    }
                }
            }
        };

    } catch (error) {
        console.error("Error en el procesamiento:", error);
        return {
            status: "error",
            message: error.message,
            debug: {
                error: error.message,
                stack: error.stack
            }
        };
    }
}

// Código principal para n8n
const items = $input.all();
if (items && items.length > 0 && items[0].json) {
    const datosImagen = items[0].json;
    return transformarYProcesarDatos(datosImagen);
} else {
    return {
        status: "error",
        message: "No se encontraron datos en la entrada",
        debug: {
            items: items
        }
    };
}

